<?php if (count($_GET) == 0) {    header('Location: /index.php');     die();}function sStr($var){ //sanitizeString Робин Никсон - Создаем динамические веб-сайты с помощью PHP, MySql, Javascript, CSS и HTML5    $var = stripslashes($var); //Удаляет экранирование символов.     $var = htmlentities($var); //Преобразует все возможные символы в соответствующие HTML-сущности  <b> -> &lt;    $var = strip_tags($var); // Удаляет HTML и PHP-теги из строки.     return $var;}?><a href='index.php'>Back to The Future</a><br><?phpextract($_GET);$res = "<br><i>Result array</i><br>";if (strlen($password) > 0) {    $ar['password'] = sStr($password);}if (strlen($password_confirm)>0) {    $ar['password_confirm'] = sStr($password_confirm);    if ($password !== $password_confirm) {        $res .= "Passwords do not match<br>";    }}if (strlen($email)>0) {    if (filter_var($email, FILTER_VALIDATE_EMAIL))    {        $ar['email'] = sStr($email);        $res .= "Email: ".$email." | Valid <br>";    }    else    {        $res .= "Email has wrong format ".$email."<br>";    }}if (strlen($phone)>0) {    $ar['phone'] = sStr($phone);    $res .= "Phone: ".$phone."<br>";}if (strlen($city)>0) {    $ar['city'] = sStr($city);    $res .= "City: ".$city."<br>";}echo $res."<br>";//Task2for ($i=2; $i<=9; $i++) {    echo $nval."*".$i."=".$nval*$i."<br>";}