<?php //Don't forget add value un URLif (strlen($_GET['language'])>0) {    $language = strval($_GET['language']);}$lang_ar = [    "Январь" => "January",    "Февраль" => "February",    "Март" => "March",    "Апрель" => "April",    "Май" => "May",    "Июнь" => "June",    "Июль" => "July",    "Август" => "August",    "Сентябрь" => "September",    "Октябрь" => "October",    "Ноябрь" => "November",    "Декабрь" => "December"];if ( stripos($language, 'ru') === 0 ) {    foreach ($lang_ar as $key => $value) {        $new_arr[] = $key;    }}elseif ( stripos($language, 'en') === 0 ) {    foreach ($lang_ar as $key => $value) {        $new_arr[] = $value;    }   }else echo "Invalid language";var_dump($new_arr);